parameters.functionScript:
let baseApi = Inputs.baseApi
let page = Inputs.page + 1 || 1;
const pageSize = Inputs.pageSize || 10;

const keyword = Inputs.keyword
const sort = Inputs.sort || 'desc'

let api = baseApi + `?populate=*&pagination[page]=${page}&pagination[pageSize]=${pageSize}&sort=createdAt:${sort}`


function createFilters(keyword) {
  if (!keyword || typeof keyword !== "string") {
    return;
  }

  const filters = [
    `filters[$or][0][Title][$contains]=${keyword}`,
    `filters[$or][1][Content][$contains]=${keyword}`,
  ];

  return filters.join("&");
}

if (keyword) {
  const apiEndpoint = createFilters(keyword);
  api += `&${apiEndpoint}`;
}

Outputs.api = api
