parameters.functionScript:
const Ip1 = Inputs.Ip1;
const Ip2 = Inputs.Ip2;
const Ip3 = Inputs.Ip3;
const Ip4 = Inputs.Ip4;
const Ip5 = Inputs.Ip5;
const Ip6 = Inputs.Ip6;
const Ip7 = Inputs.Ip7;

const error = Inputs.error

const isFormValid = Ip1 && Ip2 && Ip3 && Ip4 && Ip5 && Ip6 && Ip7;

if (error) {
    Outputs.result = false
    return
}

Outputs.result = isFormValid

const add = Inputs.add
const edit = Inputs.edit

if (edit) {
    return;
}

Outputs.Clear()
Outputs.value = '';

const file = Inputs.file;

const placeholderImage = 'https://placehold.co/100x100/38BCF8/FFF?text=UniHand';
const validImageTypes = ['image/gif', 'image/jpeg', 'image/png'];

if (file && validImageTypes.includes(file.type)) {
  const imageUrl = URL.createObjectURL(file);
  Outputs.image = imageUrl;
  Outputs.file = file;
} else {
  Outputs.image = placeholderImage;
  Outputs.file = null;
}

function clearOutputs() {
  Outputs.file = {};
  Outputs.image = 'https://placehold.co/100x100/38BCF8/FFF?text=UniHand'
}

// Gọi hàm này mỗi khi bạn muốn thực hiện hành động
clearOutputs();

const user = Inputs.user;
Outputs.hide = false;

if (!user || user?.length == 0) {
    Outputs.user = [];
    Outputs.hide = false;
    return
}else{
    Outputs.hide = true;
    Outputs.user = user;
}

