parameters.responseScript:
// Kiểm tra status của Response
if (Response.status !== 200) {
    console.error("Kiểm tra token không thành công: Status code không phải là 200");

    // Xóa thông tin nếu status không phải là 200
    Noodl.Objects.CurrentUser.token = null;
    Noodl.Objects.CurrentUser.user = null;
    Noodl.Objects.CurrentUser.authenticated = false;
    localStorage.removeItem('authToken');
    localStorage.removeItem('user');

    // Đảm bảo không có thông tin không hợp lệ được sử dụng
    Outputs.Results = null; // Hoặc một giá trị khác để không có kết quả
} else {
    // Nếu status là 200, kiểm tra nội dung
    const user = Response.content;

    console.log(user);

    if (user && Object.keys(user).length > 0 && user?.role?.name === 'Admin') {
        console.log("Kiểm tra token thành công");
        console.log(Response.content);

        // Cập nhật thông tin vào Noodl.Objects
        Noodl.Objects.CurrentUser.user = user;
        Noodl.Objects.CurrentUser.authenticated = true;
    } else {
        console.error("Kiểm tra token không thành công: Nội dung không hợp lệ");

        // Xóa thông tin nếu nội dung không hợp lệ
        Noodl.Objects.CurrentUser.token = null;
        Noodl.Objects.CurrentUser.user = null;
        Noodl.Objects.CurrentUser.authenticated = false;
        localStorage.removeItem('authToken');
        localStorage.removeItem('user');
    }

    // Xuất kết quả đầu ra
    Outputs.Results = Response.content;
    Outputs.data = Response.content;
}

