const seconds = Math.floor((now.getTime() - date.getTime()) / 1000);

const intervals = {
    "năm": 31536000,
    "tháng": 2592000,
    "tuần": 604800,
    "ngày": 86400,
    "giờ": 3600,
    "phút": 60,
    "giây": 1,
};
for (const key in intervals) {
    const interval = intervals[key];
    const count = Math.floor(seconds / interval);

    if (count >= 1) {
        Outputs.timeAgo = `${count} ${key}${count > 1 ? " trước " : ""}`;
        return;
    }
}

Outputs.timeAgo = "bây giờ";

