const page = Inputs.page + 1 || 1;
const pageSize = Inputs.pageSize || 2;
const base_url = Inputs.base_url;
const keyword = Inputs.keyword;
const sort = Inputs.sort || "desc";
const provinceId = Inputs.provinceId;
const DistrictId = Inputs.DistrictId;
const WardId = Inputs.WardId;
const DisasterId = Inputs.DisasterId

function createFilters(keyword) {
  if (!keyword || typeof keyword !== "string") {
    return;
  }

  const filters = [
    `filters[$or][1][FullName][$contains]=${keyword}`,
    `filters[$or][2][Email][$contains]=${keyword}`,
    `filters[$or][3][PhoneNumber][$contains]=${keyword}`,
    `filters[$or][4][PhoneNumber][$contains]=${keyword}`,
    `filters[$or][5][Province][ProvinceName][$contains]=${keyword}`,
    `filters[$or][6][District][DistrictName][$contains]=${keyword}`,
    `filters[$or][7][Ward][WardName][$contains]=${keyword}`,
  ];

  return filters.join("&");
}

const apiEndpoint = keyword ? createFilters(keyword) : "";

let apiUrl = `${base_url}&pagination[page]=${page}&pagination[pageSize]=${pageSize}&sort=createdAt:${sort}`;

if (DisasterId) {
  apiUrl += `&filters[Disaster]=${DisasterId}`;
}

if (provinceId) {
  apiUrl += `&filters[Province]=${provinceId}`;
}

if (DistrictId) {
  apiUrl += `&filters[District]=${DistrictId}`;
}

if (WardId) {
  apiUrl += `&filters[Ward]=${WardId}`;
}

if (apiEndpoint) {
  apiUrl += `&${apiEndpoint}`;
}

Outputs.api = apiUrl;

