    PopupMarginTop: 'number',
    Text: 'color',
    TextMuted: 'color',
    Accent: 'color',
    Background: 'color',
    SelectedDateBackground: 'color',
    SelectedDateText: 'color',
    HoverDateBackground: 'color',
    HoverDateText: 'color'
}

Script.Signals.DidMount = () => {
    Script.Outputs.IsMobile = isMobile()

    injectDatePickerStyles()

    // Register blur / focus listeners
    if(Script.Inputs.TextInput.getDOMElement() !== undefined) {
        var textInput = Script.Inputs.TextInput.getDOMElement().querySelectorAll('input')[0]
        textInput.addEventListener('focus',TextInputFocused)
        textInput.addEventListener('blur',TextInputBlurred)
        textInput.addEventListener('keydown',(e) => {
            if(e.keyCode===13) textInput.blur()
        })
        textInput.addEventListener('keyup',DateStringChanged)
    }

    // If there is a date set from start, format it 
    if(Component.Object.Date !== undefined) {
        let formattedDate = formatDate(Component.Object.Date)
        Component.Object['Formatted Date'] = formattedDate
    }


    Component.Object.on('change',(ev) => {
        if(ev.name === 'Date') {
            let date = Component.Object.Date

            // The date have changed in the component object, update the
            // date picker
            if(datepicker !== undefined) {
                datepicker.setDate(date)
            }

            if(dateInput !== undefined) {
                dateInput.value = date.getFullYear() + '-' + date.getMonth() + '-' + date.getDate()
            }

            let formattedDate = formatDate(date)
            textInput.value = formattedDate
            Component.Object['Formatted Date'] = formattedDate
        }
    })

    if(isMobile()) {
        // On Mobile create a native date input hidden
        // under the icon, clicking it will show the
        // native picker
        const elem = Script.Inputs.IconGroup.getDOMElement()

        dateInput = document.createElement('input')
        dateInput.className = 'ndl-datepicker-native-input'

        elem.appendChild(dateInput)

        dateInput.type = "date"

        dateInput.onchange = () => {
            var date = new Date(dateInput.value)
            Component.Object.Date = date
            Script.Outputs.DateSelected()
        }
    }
}

