const page = Inputs.page + 1 || 1
const pageSize = Inputs.pageSize || 2
const base_url = Inputs.base_url
const keyword = Inputs.keyword
const sort = Inputs.sort || 'desc'


function createFilters(keyword) {
    if (!keyword || typeof keyword !== "string") {
        throw new Error("Keyword không hợp lệ!");
    }

    const filters = [
        `filters[$or][0][Name][$contains]=${keyword}`, // Tìm theo trường Name
        `filters[$or][1][Description][$contains]=${keyword}`, // Tìm trong User.username
        `filters[$or][2][DisasterType][DisasterTypeName][$contains]=${keyword}`, // Tìm trong User.username
    ];

    return `${filters.join("&")}`;
}

const apiEndpoint = keyword && createFilters(keyword);

Outputs.api = `${base_url}&pagination[page]=${page}&pagination[pageSize]=${pageSize}&sort=createdAt:${sort}&${apiEndpoint ? apiEndpoint : ''}`

