parameters.functionScript:
const page = Inputs.page + 1 || 1;
const pageSize = Inputs.pageSize || 5;
const base_url = Inputs.base_url;

const keyword = Inputs.keyword;
const sort = Inputs.sort || "desc";
const status = Inputs.status;
const type = Inputs.type
const provinces = Inputs.provinces

function createFilters(keyword) {
  if (!keyword || typeof keyword !== "string") {
    throw new Error("Keyword không hợp lệ!");
  }

  const filters = [
    `filters[$or][0][Name][$contains]=${keyword}`, // Tìm theo trường Name
    `filters[$or][1][Description][$contains]=${keyword}`, // Tìm trong User.username
    `filters[$or][2][DisasterType][DisasterTypeName][$contains]=${keyword}`, // Tìm trong User.username
  ];

  return `${filters.join("&")}`;
}

let api = `${base_url}?populate=*&pagination[page]=${page}&pagination[pageSize]=${pageSize}&sort=createdAt:${sort}`;

if (keyword) {
  const apiEndpoint = keyword && createFilters(keyword);
  api += `&${apiEndpoint}`;
}

if (status) {
  let filters = [];
  // Chưa diễn ra
  if (status == "1") {
    filters = [`filters[StartDate][$gt]=${new Date().toISOString()}`];
  }

  // Đang diễn ra
  if (status == "2") {
    filters = [
      `filters[StartDate][$lte]=${new Date().toISOString()}`,
      `filters[$or][0][EndDate][$null]=true`,
      `filters[$or][1][EndDate][$gte]=${new Date().toISOString()}`,
    ];
  }

  // Đã diễn ra
  if (status == "3") {
    filters= [
        `filters[EndDate][$lt]=${new Date().toISOString()}`
    ]
  }

  api += `&${filters.join("&")}`;
}

if(provinces){
  api += `&filters[Provinces]=${provinces}`
}


if(type){
  api += `&filters[DisasterType]=${type}`
}


Outputs.api = api;

