const page = Inputs.page + 1 || 1
const pageSize = Inputs.pageSize || 5
const base_url = Inputs.base_url

const keyword = Inputs.keyword
const sort = Inputs.sort || 'desc'
const confirmed = Inputs.confirmed
const idTinh = Inputs.idTinh


let api = base_url + `?populate=*&pagination[page]=${page}&pagination[pageSize]=${pageSize}&filters[user][$notNull]=true&sort=createdAt:${sort}`


function createFilters(keyword) {
    if (!keyword || typeof keyword !== "string") {
        throw new Error("Keyword không hợp lệ!");
    }

    const filters = [
        `filters[$or][0][Name][$contains]=${keyword}`, // Tìm theo trường Name
        `filters[$or][1][user][username][$contains]=${keyword}`, // Tìm trong User.username
        `filters[$or][2][user][email][$contains]=${keyword}` // Tìm trong User.email
    ];

    return `${filters.join("&")}`;
}


if (keyword) {
    const apiEndpoint = createFilters(keyword);
    api += `&${apiEndpoint}`;
}

if (idTinh) {
    api += `&filters[Province]=${idTinh}`;
}


if (confirmed) {
    const tam = confirmed === 'true' ? true : false
    if (tam == true) {
        api += `&filters[Confirmed]=true`;
    } else {
        api += `&filters[$or][0][Confirmed]=false&filters[$or][1][Confirmed][$null]=true`;
    }
}

Outputs.api = api

