let page = Inputs.page + 1 || 1;
const pageSize = Inputs.pageSize || 5;
const base_url = Inputs.base_url;

const keyword = Inputs.keyword;
const sort = Inputs.sort || "desc";
const ProvinceId = Inputs.ProvinceId;
const DistrictId = Inputs.DistrictId;
const WardId = Inputs.WardId;
const DisasterId = Inputs.DisasterId;
const status = Inputs.status;


let apiUrl = `${base_url}?populate=*&pagination[page]=${page}&pagination[pageSize]=${pageSize}&sort=createdAt:${sort}`;

function createFilters(keyword) {
  if (!keyword || typeof keyword !== "string") {
    return;
  }

  const filters = [
    `filters[$or][1][FullName][$contains]=${keyword}`,
    `filters[$or][2][Email][$contains]=${keyword}`,
    `filters[$or][3][RequestDescription][$contains]=${keyword}`,
    `filters[$or][4][PhoneNumber][$contains]=${keyword}`,
    `filters[$or][5][PhoneNumber][$contains]=${keyword}`,
    `filters[$or][6][Province][FullName][$contains]=${keyword}`,
    `filters[$or][8][District][FullName][$contains]=${keyword}`,
    `filters[$or][9][Ward][FullName][$contains]=${keyword}`,
  ];

  return filters.join("&");
}

const apiEndpoint = keyword ? createFilters(keyword) : "";

if (DisasterId) {
  if (DisasterId == "-1") {
    apiUrl = apiUrl;
  } else {
    apiUrl += `&filters[Disaster]=${DisasterId}`;
  }
}

if (ProvinceId) {
  apiUrl += `&filters[Province]=${ProvinceId}`;
}

if (DistrictId) {
  apiUrl += `&filters[District]=${DistrictId}`;
}

if (WardId) {
  apiUrl += `&filters[Ward]=${WardId}`;
}

if (status) {
  let filters = [];
  // Chưa hổ trợ
  if (status == '1') {
    filters = [
      `filters[AcceptedBy][$null]=true`
    ];
  } else if (status == '2') { //Đang hổ trợ
    filters = [
      `filters[$or][1][State]=false`,
      `filters[$or][2][State][$null]=true`,
      `filters[AcceptedBy][$notNull]=true`
    ];
  } else if (status == '3') {  // Đã hổ trợ
    filters = [
      `filters[$or][1][State]=true`
    ];
  }

  apiUrl += `&${filters.join("&")}`;
}

if (apiEndpoint) {
  apiUrl += `&${apiEndpoint}`;
}

Outputs.api = apiUrl;

