// Hàm tạo bảng HTML từ JSON
function generateTable(data) {
  let table = "<table style='width: 100%; border-collapse: collapse; border: 1px solid black;'>";

  // Tạo tiêu đề cột (headers)
  table += "<thead><tr>";
  Object.keys(data[0]).forEach((key) => {
    table += `<th style='border: 1px solid black; padding: 8px; text-align: left;'>${key}</th>`;
  });
  table += "</tr></thead>";

  // Tạo nội dung bảng
  table += "<tbody>";
  data.forEach((row) => {
    table += "<tr>";
    Object.values(row).forEach((value) => {
      table += `<td style='border: 1px solid black; padding: 8px;'>${value}</td>`;
    });
    table += "</tr>";
  });
  table += "</tbody></table>";

  return table;
}

// Hàm in bảng ngay khi chạy
function printTableImmediately() {
  const tableContent = generateTable(data); // Tạo bảng từ dữ liệu đã map

  const currentDateTime = new Date().toLocaleString(); // Lấy thời gian hiện tại
  const recordCount = data.length; // Số lượng bản ghi

  const printWindow = window.open("", "", "width=800,height=600"); // Mở cửa sổ in
  printWindow.document.write("<html><head><title>In bảng</title></head><body>");

  // Thêm tiêu đề và thời gian
  printWindow.document.write("<h1 style='text-align: center;'>Danh sách</h1>");
  printWindow.document.write("<h4 style='text-align: center;'>UniHand</h4>");
  printWindow.document.write(`<p style='text-align: center;'>Thời gian in: ${currentDateTime}</p>`);
  printWindow.document.write(`<p style='text-align: center;'>Tổng số: ${recordCount}</p>`);

  // Chèn nội dung bảng
  printWindow.document.write(tableContent);

  printWindow.document.write("</body></html>");
  printWindow.document.close();
  printWindow.print(); // Gọi lệnh in
  // printWindow.close(); // Đóng cửa sổ sau khi in
}

// Gọi hàm in bảng
data && printTableImmediately();

