function extractProvinces(data) {
    const seen = new Set();
    return data.flatMap((item) =>
        item.Provinces.map((province) => ({
            name: province.FullName,
            location: {
                lat: parseFloat(province.Latitude) || 0,
                lng: parseFloat(province.Longitude) || 0,
            },
        }))
    ).filter(province => {
        if (seen.has(province.name)) {
            return false;
        } else {
            seen.add(province.name);
            return true;
        }
    });
}

if (data) {
    const provinces = extractProvinces(data);

    const oldProvinces = JSON.parse(localStorage.getItem("provinces")) || [];

    if (JSON.stringify(provinces) === JSON.stringify(oldProvinces)) {
        Outputs.Success = true
        return;
    } else {
        if (provinces !== undefined) {
            localStorage.setItem("provinces", JSON.stringify(provinces));
