let page = Inputs.Page + 1 || 1;
const pageSize = Inputs.PageSize || 5;
const API_URL = Inputs.API_URL;
const organizationId = Inputs.OrganizationId;
const keyword = Inputs.Keyword;
const sort = Inputs.Sort || "desc";
const provinceId = Inputs.ProvinceId;
const districtId = Inputs.DistrictId;
const wardId = Inputs.WardId;
const disasterId = Inputs.DisasterId;
const statusId = Inputs.Status;

let apiUrl = `${API_URL}/notifications`
+ `?filters[SupportOrganization]=${organizationId}`
+ `&populate[SOSRequest][populate]=*`
+ `&pagination[page]=${page}`
+ `&pagination[pageSize]=${pageSize}`
+ `&sort=createdAt:${sort}`;

function createFilters(keyword) {
  if (!keyword || typeof keyword !== "string") {
    return;
  }

  const filters = [
    `filters[$or][1][SOSRequest][FullName][$contains]=${keyword}`,
    `filters[$or][2][SOSRequest][Email][$contains]=${keyword}`,
    `filters[$or][3][SOSRequest][RequestDescription][$contains]=${keyword}`,
    `filters[$or][4][SOSRequest][PhoneNumber][$contains]=${keyword}`,
    `filters[$or][5][SOSRequest][PhoneNumber][$contains]=${keyword}`,
    `filters[$or][6][SOSRequest][Province][FullName][$contains]=${keyword}`,
    `filters[$or][8][SOSRequest][District][FullName][$contains]=${keyword}`,
    `filters[$or][9][SOSRequest][Ward][FullName][$contains]=${keyword}`,
  ];

  return filters.join("&");
}

const apiEndpoint = keyword ? createFilters(keyword) : "";

if (disasterId) {
  apiUrl += `&filters[SOSRequest][Disaster]=${disasterId}`;
}

if (provinceId) {
  apiUrl += `&filters[SOSRequest][Province]=${provinceId}`;
}

if (districtId) {
  apiUrl += `&filters[SOSRequest][District]=${districtId}`;
} 

if (wardId) {
  apiUrl += `&filters[SOSRequest][Ward]=${wardId}`;
}

if (statusId) {
  let filters = [];
  // Chưa hổ trợ
  if (statusId == '1') {
    filters = [
      `filters[SOSRequest][AcceptedBy][$null]=true`
    ];
  } else if (statusId == '2') { //Đang hổ trợ
    filters = [
      `filters[$or][1][SOSRequest][State]=false`,
      `filters[$or][2][SOSRequest][State][$null]=true`,
      `filters[SOSRequest][AcceptedBy][$notNull]=true`
    ];
  } else if (statusId == '3') {  // Đã hổ trợ
    filters = [
      `filters[$or][1][SOSRequest][State]=true`
    ];
  }

  apiUrl += `&${filters.join("&")}`;
}

if (apiEndpoint) {
  apiUrl += `&${apiEndpoint}`;
}

Outputs.API = apiUrl;

