const data = Inputs.data

if (!data) return

Outputs.FullName = data?.FullName
Outputs.RequestDescription = data?.RequestDescription;
Outputs.PeopleCount = data?.PeopleCount;
Outputs.NeedWater = data?.NeedWater;
Outputs.NeedFood = data?.NeedFood;
Outputs.NeedMedical = data?.NeedMedical;
Outputs.Location = data?.Location;
Outputs.PhoneNumber = data?.PhoneNumber;
Outputs.Email = data?.Email;
Outputs.Province = data?.Province;
Outputs.District = data?.District;
Outputs.Ward = data?.Ward;
Outputs.Disaster = data?.Disaster;
Outputs.DisasterDescription = data?.Disaster?.Description;
Outputs.DamageImage = data?.DamageImage
Outputs.State = data?.State
Outputs.AcceptedBy = data?.AcceptedBy
Outputs.AudioFile = data?.AudioFile

const provinceName = Inputs.Province?.FullName || ""
const districtName = Inputs.District?.FullName || ""
const wardName = Inputs.Ward?.FullName || ""

const parts = [];


if (wardName) parts.push(wardName);
if (districtName) parts.push(districtName);
if (provinceName) parts.push(provinceName);

if (parts.length > 0) {
    const address = `${parts.join(", ")}`; // Ghép các phần tử không rỗng
    Outputs.Address = address
}

const state = Inputs.state || false
const AcceptedBy = Inputs.AcceptedBy

Outputs.ChuaHoTro = false;
Outputs.DangHoTro = false;
Outputs.DaHoTro = false;

if (!state && AcceptedBy === null) {
    Outputs.ChuaHoTro = true;
    return
} else if (!state && AcceptedBy !== null) {
    Outputs.DangHoTro = true;
    return
} else if (state) {
    Outputs.DaHoTro = true;
    return;
}

