const Data = Inputs.Data;

if (Data) {

    Outputs.Disaster = Data.Disaster

    if (Data?.Location?.lat == 0 && Data?.Location?.lat == 0) {
        localStorage.removeItem('locations')
        return
    }

    const locations = [
        {
            description: Data.FullName,
            phone: Data.PhoneNumber,
            lat: Data.Location?.lat ? parseFloat(Data.Location.lat) : 0,
            lng: Data.Location?.lat ? parseFloat(Data.Location.lng) : 0,
        },
    ];

    const oldLocations = JSON.parse(localStorage.getItem("locations")) || [];

    if (JSON.stringify(locations) === JSON.stringify(oldLocations)) {
        return;
    } else {
        localStorage.setItem("locations", JSON.stringify(locations));
        Outputs.Success();
        return;
    }
}

