parameters.functionScript:
const date = new Date(Inputs.Date)

const now = new Date();

const seconds = Math.floor((now.getTime() - date.getTime()) / 1000);

const intervals = {
    "năm": 31536000,
    "tháng": 2592000,
    "tuần": 604800,
    "ngày": 86400,
    "giờ": 3600,
    "phút": 60,
    "giây": 1,
};
for (const key in intervals) {
    const interval = intervals[key];
    const count = Math.floor(seconds / interval);

    if (count >= 1) {
        Outputs.TimeAgo = `${count} ${key}${count > 1 ? " trước " : ""}`;
        return;
    }
}

Outputs.TimeAgo = "bây giờ";

const data = Inputs.data;

function extractProvinces(data) {
    const seen = new Set();
    return data.flatMap((item) =>
        item.Provinces.map((province) => ({
            name: province.FullName,
            location: {
                lat: parseFloat(province.Latitude) || 0,
                lng: parseFloat(province.Longitude) || 0,
            },
        }))
    ).filter(province => {
        if (seen.has(province.name)) {
            return false;
        } else {
            seen.add(province.name);
            return true;
        }
    });
}

if (data) {
    const provinces = extractProvinces(data);

    const oldProvinces = JSON.parse(localStorage.getItem("provinces")) || [];

    if (JSON.stringify(provinces) === JSON.stringify(oldProvinces)) {
        return;
    } else {
        localStorage.setItem("provinces", JSON.stringify(provinces));
        Outputs.Success();
        return;
    }
}

const file = Inputs.file

if (file) {
    const formData = new FormData();

    formData.append("files", file);

    fetch("http://194.233.66.115:1337/api/upload", {
        method: "POST",
        body: formData,
    })
        .then((response) => response.json())
        .then((data) => {
            Outputs.data = data[0].id
            Outputs.Success()
        })
        .catch((error) => {
            Outputs.Failute()
        });
} else {
    Outputs.data = null
    Outputs.Failute()
}

navigator.geolocation.getCurrentPosition(async (position) => {
    const { latitude, longitude } = position.coords;

    if(latitude && longitude) {
        Outputs.lat = latitude;
        Outputs.lng = longitude;
        Outputs.Success();
    }
    else {
        Outputs.Failure();
    }

    navigator.geolocation.watchPosition(
        (error) => {
            console.error("Lỗi lấy vị trí:", error);
        },
        {
            enableHighAccuracy: true,
            maximumAge: 300
        }
    );
});

// No controls, just exit with empty array
if(Inputs.Controls === undefined) {
    Component.Object.ControlItems = []
    return
}

// If there is no object id provided, create a new object for form values
let formValues
if(!Inputs.ObjectId || !Noodl.Object.exists(Inputs.ObjectId)) {
    formValues = Component.Object.FormValues || Noodl.Object.create({})
}
else {
    formValues = Noodl.Objects[Inputs.ObjectId]
}

Component.Object.FormValues = formValues
Outputs.ObjectId = formValues.getId()
const errors = Component.Object.Errors = Inputs.Errors

// Create the control items array (all form controls)
Component.Object.ControlItems = Inputs.Controls.map(c => {
    // Errors should only be shown for a control if it has
    // been edited, or if the form has been submitted (show all errors)
    const haveValues = Component.Object.HaveValues||{}
    const showAllErrors = Component.Object.ShowAllErrors
    const error = (errors&&(haveValues[c.Property]||showAllErrors))?errors.find(e => e.Property === c.Property):undefined

    const controlItem = {
        id:Component.Object.id + '-' + c.Property,
        Value:formValues[c.Property],
        ObjectId:formValues.getId(),
        Error:error?error.Error:""
    }

    for(let key in c) { // Copy non function values to the new object
        if(typeof c[key] !== "function" && typeof c[key] !== "undefined")
            controlItem[key] = c[key]
    }

    return Noodl.Object.create(controlItem)
})

