parameters.functionScript:
Noodl.Events.on(Component.Object.id + '-Form.ValueChanged',(ev) => {
    Component.Object.HaveValues[ev.Property] = true

    Outputs.Property = ev.Property
    Outputs.Changed()
})

const errors = Component.Object.Errors
const controlItems = Component.Object.ControlItems

// To be valid there should be no errors for any
// controls
const isValid = errors === undefined || errors.length === 0 || errors.every(e => !controlItems.find(c => c.Property === e.Property))

if(isValid) {
    Outputs.Validated()
}
else {
    // There were errors, since the user tried to submit
    // show all errors
    Component.Object.ShowAllErrors = true
    controlItems.forEach(c => {
        const error = errors.find(e => e.Property === c.Property)
        c.Error = error?error.Error:""
    })
    Outputs.HaveErrors()
}

