const pages = Inputs.RowPerPageOptions;

Outputs.Pages = pages.map(p => ({
    Label:""+p,
    Value:""+p
}))

const rowsPerPage = parseInt(Inputs.Value);

Component.Object.RowsPerPage = isNaN(rowsPerPage)?5:rowsPerPage;

Outputs.Changed();

const startRow = Inputs.RowsPerPage*Inputs.SelectedPage;
let endRow = startRow + Inputs.RowsPerPage;

if(endRow > Inputs.TotalCount)
    endRow = Inputs.TotalCount;

const numPages = Math.ceil(Inputs.TotalCount / Inputs.RowsPerPage);
Component.Object.NumPages = numPages;

if(Component.Object['Selected Page'] > numPages-1) {
    Component.Object['Selected Page'] = numPages-1;
}

Outputs.NextEnabled = Component.Object['Selected Page'] < numPages-1;
Outputs.BackEnabled = Component.Object['Selected Page'] > 0;

Outputs.Text = (startRow+1) + "-" + endRow + " of " + (Inputs.TotalCount!==undefined?Inputs.TotalCount:"?");

if(Component.Object['Selected Page'] > 0)
    Component.Object['Selected Page']--;

Outputs.Changed();

if(Component.Object['Selected Page'] < Component.Object.NumPages-1)
    Component.Object['Selected Page']++;

Outputs.Changed();

