const params = {
    client_id:Inputs.ClientId,
    response_type:'code',
    redirect_uri:Inputs.RedirectUri,
    scope:Inputs.Scope,
}

const query = Object.keys(params).map(key => key + "=" + encodeURIComponent(params[key])).join('&')

Outputs.Url = Inputs.AuthEndpoint + "?" + query

const params = {
    client_id:Inputs.ClientId,
    client_secret:Inputs.ClientSecret,
    grant_type:'authorization_code',
    code:Inputs.Code,
    redirect_uri:Inputs.RedirectUri,
    scope:Inputs.Scope
}

const query = Object.keys(params).map(key => key + "=" + params[key]).join('&')

const res = await fetch(Inputs.TokenEndpoint,{
    method:'POST',
    headers:{
        'content-type':'application/x-www-form-urlencoded',
        'content-length':query.length,
    },
    body:query,
})

if(res.ok) {
    const json = await res.json()

    // We have an access token
    const accessToken = json.access_token;
    if(!accessToken) {
        Outputs.Failure()
        throw Error("No access token in response")
    }

    const oauthUser = await fetch(Inputs.UserEndpoint,{
        method:'GET',
        headers:{
            Authorization:'Bearer ' + accessToken
        }  
    })

    if(oauthUser.ok) {
        const userJson = await oauthUser.json()

        const email = userJson[Inputs.EmailField];
        if(!accessToken) {
            Outputs.Failure()
            throw Error("No email found in user response.")
        }

        const users = await Noodl.Records.query("_User",{
            email:{equalTo:email}
        })

        if(users && users.length === 1) {
            // We already have a user, add to auth data
            // great, impersonate and return
            const _user = await Noodl.Users.impersonate(users[0].username)
            Outputs.SessionToken = _user.sessionToken;
            Outputs.Success()
        }
        else if(!users || users.length === 0) {
            // There is no user, create one
            const _user = await Noodl.Records.create("_User",{
                username:email,
                email:email,
                password:(Math.random() + 1).toString(36).substring(7), // Random fire and forget password
            },{
                acl:{
                    '*':{read:false,write:false}
                }
            })
            Outputs.SessionToken = _user.sessionToken;
            Outputs.Success()
        }
    }
    else {
        Outputs.Failure();
        throw Error("Failed to fetch user from user endpoint.")
    }
}
else {
    Outputs.Failure();
    throw Error("Failed to fetch token with authorization code.")
}

const tokenInfo = localStorage.getItem("authToken"); // Lấy trạng thái từ Local Storage

if (tokenInfo) {
  Outputs.Success = true;
} else {
  Outputs.Failure = false;
}

