// If there is no object id provided, create a new object for form values
let formValues
if(!Inputs.ObjectId || !Noodl.Object.exists(Inputs.ObjectId)) {
    formValues = Component.Object.FormValues || Noodl.Object.create({})
}
else {
    formValues = Noodl.Objects[Inputs.ObjectId]
}

Component.Object.FormValues = formValues
Outputs.ObjectId = formValues.getId()
const errors = Component.Object.Errors = Inputs.Errors

// Create the control items array (all form controls)
Component.Object.ControlItems = Inputs.Controls.map(c => {
    // Errors should only be shown for a control if it has
    // been edited, or if the form has been submitted (show all errors)
    const haveValues = Component.Object.HaveValues||{}
    const showAllErrors = Component.Object.ShowAllErrors
    const error = (errors&&(haveValues[c.Property]||showAllErrors))?errors.find(e => e.Property === c.Property):undefined

    const controlItem = {
        id:Component.Object.id + '-' + c.Property,
        Value:formValues[c.Property],
        ObjectId:formValues.getId(),
        Error:error?error.Error:""
    }

    for(let key in c) { // Copy non function values to the new object
        if(typeof c[key] !== "function" && typeof c[key] !== "undefined")
            controlItem[key] = c[key]
    }

    return Noodl.Object.create(controlItem)
})

